    private void btnSumarActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnSumarActionPerformed
        int n1 = Integer.parseInt(txtN1.getText());
        int n2 = Integer.parseInt(txtN2.getText());
        int suma = n1 + n2;
        lbResultado.setText(suma + "");
    }//GEN-LAST:event_btnSumarActionPerformed

    private void btnRestarActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnRestarActionPerformed
        int n1 = Integer.parseInt(txtN1.getText());
        int n2 = Integer.parseInt(txtN2.getText());
        int resta = n1 - n2;
        lbResultado.setText(resta + "");
    }//GEN-LAST:event_btnRestarActionPerformed

    private void btnMultiplicarActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnMultiplicarActionPerformed
        int n1 = Integer.parseInt(txtN1.getText());
        int n2 = Integer.parseInt(txtN2.getText());
        int multiplicar = n1 * n2;
        lbResultado.setText(multiplicar + "");
    }//GEN-LAST:event_btnMultiplicarActionPerformed

    private void btnDividirActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnDividirActionPerformed
        int n1 = Integer.parseInt(txtN1.getText());
        int n2 = Integer.parseInt(txtN2.getText());
        double dividir = n1 / n2;
        lbResultado.setText(dividir + "");
    }//GEN-LAST:event_btnDividirActionPerformed

    private void btnSinActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnSinActionPerformed
        int n1 = Integer.parseInt(txtNum.getText());
        double sin = Math.sin(n1);
        lbResultados.setText(sin + "");
    }//GEN-LAST:event_btnSinActionPerformed

    private void btnCosActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnCosActionPerformed
        int n1 = Integer.parseInt(txtNum.getText());
        double cos = Math.cos(n1);
        lbResultados.setText(cos + "");
    }//GEN-LAST:event_btnCosActionPerformed

    private void btnTanActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnTanActionPerformed
        int n1 = Integer.parseInt(txtNum.getText());
        double tan = Math.tan(n1);
        lbResultados.setText(tan + "");
    }//GEN-LAST:event_btnTanActionPerformed

    private void btnAbsoluteActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnAbsoluteActionPerformed
        int n1 = Integer.parseInt(txtNum.getText());
        double abs = Math.abs(n1);
        lbResultados.setText(abs + "");
    }//GEN-LAST:event_btnAbsoluteActionPerformed

    private void btnRedondeoActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnRedondeoActionPerformed
        double n1 = Double.parseDouble(txtNum.getText());
        double round = Math.round(n1);
        lbResultados.setText(round + "");
    }//GEN-LAST:event_btnRedondeoActionPerformed

